'use strict';

var utils = require('../../../utils/utils');
var BaseXform = require('../base-xform');

var TextXform = module.exports = function () {};

utils.inherits(TextXform, BaseXform, {

  get tag() {
    return 't';
  },

  render: function render(xmlStream, model) {
    if (model) {
      xmlStream.leafNode(this.tag, model.attributes ? model.attributes: null, model.text);
    }
  },

  parseOpen: function parseOpen(node) {
    if (node.name == this.tag) {
      this.model = {}
      this.model.attributes = node.attributes;
    }
  },
  parseText: function parseText(text) {
    if (this.parser) {
      this.parser.parseText(text);
    }
    this.model.text = text;
  },
  parseClose: function parseClose() {},
});
